import React from 'react';

import Spinner from '../spinner/spinner.component';

const WithSpinner = WrappedComponent => {
    const Spinner = ({ isLoading, ...otherProps }) => {
    return isLoading ? (
      <Spinner />
    ) : (
      <WrappedComponent {...otherProps} />
    );
  }
  return Spinner;
};

export default WithSpinner;
